    // CÁCH 1
int helper(string &s) {
    int i =  s.find("()");
    if(i < 0) return s.length();
    s=s.substr(0,i) + s.substr(i+2,s.length()-(i+2));
    return helper(s);
}

int mininumBracketAdd(string s) {
    string tmp ="";
    
    return helper(s);
}




